protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        //假设的条件：根据这个条件决定是否隐藏列\n        bool needToHideColumns = true;\n        \n        if (needToHideColumns)\n        {\n            HideLastTwoColumns();\n        }\n    }\n}\n\nprivate void HideLastTwoColumns()\n{\n    foreach (GridViewRow row in GridView1.Rows)\n    {\n        // Hide the last two cell columns for each row\n        if (row.Cells.Count > 3)\n        {\n            row.Cells[2].Visible = false; // Hides third column from view\n            row.Cells[3].Visible = false; // Hides fourth column from view\n        }\n    }\n\n    // Optionally hide the header cells of the last two columns\n    if (GridView1.HeaderRow != null && GridView1.HeaderRow.Cells.Count > 3)\n    {\n        GridView1.HeaderRow.Cells[2].Visible = false;\n        GridView1.HeaderRow.Cells[3].Visible = false;\n    }\n}