using System;\nusing System.Collections.Generic;\n\nclass Program {\n    static void Main() {\n        List<int> numbers = new List<int>{ 5, 3, 8, 6, 2, 7, 4, 1 };\n\n        // Sorting the list using the default comparer\n        numbers.Sort();\n\n        Console.WriteLine("Sorted list:");\n        foreach (var number in numbers) {\n            Console.Write(number + " ");\n        }\n    }\n}