using System;\nusing System.Windows.Forms;\n\npublic class InfoPathFormViewer : Form\n{\n    private WebBrowser webBrowser;\n\n    public InfoPathFormViewer()\n    {\n        // Initialize the WebBrowser control\n        webBrowser = new WebBrowser();\n        webBrowser.Dock = DockStyle.Fill;\n\n        // Navigate to the SharePoint site where the form is published\n        webBrowser.Navigate("https://yoursharepointsite.com/library/form.xsn");\n\n        // Add the WebBrowser control to the Form\n        this.Controls.Add(webBrowser);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new InfoPathFormViewer());\n    }\n}