using (var db = new YourDataContext())\n{\n    // Create a new instance of Fruit with the ID you have.\n    var fruitToUpdate = new Fruit { FruitID = yourFruitId };\n    \n    // Attach the entity to the context and specify that we are going to update it.\n    db.Fruits.Attach(fruitToUpdate, true);\n    \n    // Set the properties you want to update.\n    fruitToUpdate.FruitName = "New Fruit Name";\n    fruitToUpdate.FruitStatusID = null; // Set to null or use (int?)null if necessary\n    \n    // Submit changes to the database.\n    db.SubmitChanges();\n}