private void textBox1_TextChanged(object sender, EventArgs e)\n{\n    string yourSentence = textBox1.Text.ToLower().Trim();\n\n    char ch1 = 'a';\n    char ch2 = 'e';\n    char ch3 = 'i';\n    char ch4 = 'o';\n    char ch5 = 'u';\n\n    int counta = 0;\n    int counte = 0;\n    int counti = 0;\n    int counto = 0;\n    int countu = 0;\n\n    foreach (char v in yourSentence)\n    {\n        if (v == ch1) counta++;\n        else if (v == ch2) counte++;\n        else if (v == ch3) counti++;\n        else if (v == ch4) counto++;\n        else if (v == ch5) countu++;\n    }\n\n    int j = counta + counte + counti + counto + countu;\n\n    listBox1.Items.Clear(); // Clear previous counts\n    listBox1.Items.Add("There are " + counta.ToString() + " a's in the sentence");\n    listBox1.Items.Add("There are " + counte.ToString() + " e's in the sentence");\n    listBox1.Items.Add("There are " + counti.ToString() + " i's in the sentence");\n    listBox1.Items.Add("There are " + counto.ToString() + " o's in the sentence");\n    listBox1.Items.Add("There are " + countu.ToString() + " u's in the sentence");\n    listBox1.Items.Add("All in all there are " + j.ToString() + " vowels in the sentence");\n}\n\n// Optionally, you can remove or modify btnCount_Click as needed\nprivate void btnCount_Click(object sender, EventArgs e)\n{\n    // This method becomes redundant with TextChanged event\n}