using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        // Configure the proxy\n        WebProxy proxy = new WebProxy("http://your.proxy.address:port");\n        \n        // If your proxy requires credentials, you can set them like so:\n        proxy.Credentials = new NetworkCredential("username", "password");\n\n        // Create a WebClient and use the proxy settings\n        using (WebClient client = new WebClient())\n        {\n            client.Proxy = proxy;\n            \n            try\n            {\n                string content = client.DownloadString("http://yourwebsite.com/yourwebservice");\n                Console.WriteLine(content);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n        }\n    }\n}