using System.Configuration;\nusing System.Web.Configuration;\n\npublic void AddAuthorizationRule(string path, string roleName)\n{\n    // Open the web configuration file for the current application.\n    Configuration config = WebConfigurationManager.OpenWebConfiguration("~");\n\n    // Get the <authorization> section.\n    AuthorizationSection authSection = (AuthorizationSection)config.GetSection("system.web/authorization");\n\n    if (authSection != null)\n    {\n        // Create a new AuthorizationRule\n        AuthorizationRule newRule = new AuthorizationRule(AuthorizationRuleAction.Allow);\n\n        // Set the Role and Path properties of the new rule.\n        newRule.Roles.Add(roleName);\n        newRule.Path = path;\n\n        // Add the new rule to the AuthorizationSection.\n        authSection.Rules.Add(newRule);\n\n        // Save the changes to the web.config file.\n        config.Save(ConfigurationSaveMode.Modified);\n    }\n}