using Moq;\nusing NUnit.Framework;\n\n[TestFixture]\npublic class MapinfoWrapperTests\n{\n    [Test]\n    public void OpenTable_Should_Call_Do_Method_With_Correct_Command()\n    {\n        // Arrange\n        var mockMapInfo = new Mock<Mapinfo>();\n        var wrapper = new MapinfoWrapper(mockMapInfo.Object);\n        string expectedPath = "C:\\Temp\\MyTable.TAB";\n        string expectedCommand = $"OpenTable(\"{expectedPath}\")";\n\n        // Act\n        wrapper.OpenTable(expectedPath);\n\n        // Assert\n        mockMapInfo.Verify(m => m.Do(expectedCommand), Times.Once());\n    }\n}