using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Windows;\n\nnamespace WpfAppTwitterApi\n{\n    public partial class MainWindow : Window\n    {\n        private static readonly HttpClient client = new HttpClient();\n        private const string TwitterUserTimelineUrl = "https://api.twitter.com/2/tweets";\n\n        public MainWindow()\n        {\n            InitializeComponent();\n            LoadTweetsAsync("twitter_username_here");\n        }\n\n        private async Task LoadTweetsAsync(string username)\n        {\n            try\n            {\n                client.DefaultRequestHeaders.Add("Authorization", "Bearer YOUR_BEARER_TOKEN");\n\n                // Note: The endpoint and parameters need to be adjusted according to the latest Twitter API documentation.\n                var response = await client.GetAsync($"{TwitterUserTimelineUrl}?username={username}&tweet.fields=created_at");\n                response.EnsureSuccessStatusCode();\n\n                string responseBody = await response.Content.ReadAsStringAsync();\n                textBlockTweets.Text = responseBody;\n            }\n            catch (HttpRequestException e)\n            {\n                textBlockTweets.Text = "Request error: " + e.Message;\n            }\n        }\n    }\n}