using System;\nusing System.IO;\nusing MSImaging.Interop;\n\nclass DvdWriterExample\n{\n    public void WriteToDVD(MsftDiscRecorder2 recorder, MsftFileSystemImage fileSystem)\n    {\n        var session = new MsftDiscFormat2Data();\n        session.Recorder = recorder;\n        session.ForceMediaToBeClosed = false; // This keeps the disc open for further sessions\n\n        using (var writer = new MediaWriter())\n        {\n            writer.Recorder = recorder;\n\n            while (true)\n            {\n                // Simulate grabbing live data\n                var dataChunk = GrabDataFromCamera(); \n\n                if (dataChunk.Length == 0) break; // End of data stream\n\n                // Write each chunk as a separate file if necessary or build up in memory and write larger chunks\n                // For simplicity, assuming you are writing a byte array out to disk directly here, \n                // but ideally this would be done continuously without saving intermediate files.\n                string filePath = Path.Combine("TempFolder", Guid.NewGuid().ToString() + ".bin");\n                File.WriteAllBytes(filePath, dataChunk);\n\n                // Add file to the filesystem image\n                fileSystem.AddFile(filePath, true);\n            }\n\n            // Prepare and write the disc\n            var result = session.Build(fileSystem.ResultantImage);\n            session.Write(result);\n        }\n    }\n\n    private byte[] GrabDataFromCamera()\n    {\n        // Simulate data from camera\n        return new byte[1024 * 1024]; // Example chunk size of 1MB\n    }\n}