using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseAccessExample\n{\n    public void SelectData()\n    {\n        // Step 2: Create a connection string (replace placeholders with actual values)\n        string connectionString = "Server=myServerAddress;Database=myDataBase;User Id=myUsername;Password=myPassword;";\n\n        // Step 3: Instantiate SqlConnection using the connection string\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            try\n            {\n                // Step 4: Open the connection to the database\n                connection.Open();\n\n                // Define a SQL command\n                string query = "SELECT * FROM Products";\n                \n                // Step 3.5: Instantiate SqlCommand with the command text and connection\n                using (SqlCommand command = new SqlCommand(query, connection))\n                {\n                    // Step 5: Execute the command and read data\n                    using (SqlDataReader reader = command.ExecuteReader())\n                    {\n                        while (reader.Read())\n                        {\n                            Console.WriteLine("Product ID: " + reader["ProductID"] + \n                                              ", Name: " + reader["ProductName"]);\n                        }\n                    } // End of SqlDataReader - automatically closed here\n                } // End of SqlCommand object lifetime\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n            finally\n            {\n                // Step 6: Close the connection (automatically done by 'using' statement in this case)\n                if (connection.State == System.Data.ConnectionState.Open)\n                {\n                    connection.Close();\n                }\n            }\n        } // End of SqlConnection object lifetime - automatically closed here\n    }\n}