using EnvDTE;\nusing System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            // Change "VisualStudio.DTE.8.0" to the appropriate version for your installed Visual Studio.\n            Type dteType = Type.GetTypeFromProgID("VisualStudio.DTE.16.0"); // Example for VS 2019\n            object dteObject = Activator.CreateInstance(dteType);\n            DTE dte = (DTE)dteObject;\n\n            // Now you can interact with the Visual Studio instance.\n            dte.Visible = true; // Make sure it's visible to test\n            dte.ExecuteCommand("File.NewProject"); // Example command\n\n        }\n        catch (COMException ex)\n        {\n            Console.WriteLine("Error starting Visual Studio: " + ex.Message);\n        }\n    }\n}