using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private ToolTip toolTip = new ToolTip();\n    private bool isToolTipVisible = false;\n\n    public MyForm()\n    {\n        // Example control to attach the tooltip to\n        Button button = new Button { Text = "Hover me!", Size = new System.Drawing.Size(100, 50) };\n        button.MouseHover += (sender, e) => ShowToolTip(button);\n        button.MouseLeave += (sender, e) => HideToolTip();\n\n        this.Controls.Add(button);\n\n        // Configure the tooltip\n        toolTip.AutoPopDelay = 3000; // Tooltip stays open for 3 seconds\n    }\n\n    private void ShowToolTip(Control control)\n    {\n        if (!isToolTipVisible)\n        {\n            toolTip.Show("This is a tooltip.", control, new System.Drawing.Point(25, -10));\n            isToolTipVisible = true;\n        }\n    }\n\n    private void HideToolTip()\n    {\n        if (isToolTipVisible)\n        {\n            toolTip.Hide(this);\n            isToolTipVisible = false;\n        }\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MyForm());\n    }\n}