public class ExampleController : ApiController\n{\n    private readonly IComService _comService;\n\n    public ExampleController()\n    {\n        // Assuming there's a .NET wrapper around the COM component\n        _comService = new ComLibrary.ComService();\n    }\n\n    [HttpGet]\n    [Route("query")]\n    public async Task<IHttpActionResult> GetDataAsync()\n    {\n        try\n        {\n            var result = await Task.Run(() => _comService.QueryDatabase());\n            return Ok(result);\n        }\n        catch (Exception ex)\n        {\n            return InternalServerError(ex);\n        }\n    }\n}