public class RegistrationController : Controller\n{\n    [HttpGet]\n    public ActionResult Create()\n    {\n        var accountViewModel = new AccountViewModel();\n        return View(accountViewModel);\n    }\n\n    [HttpPost]\n    public ActionResult Create(AccountViewModel accountVM)\n    {\n        if (ModelState.IsValid)\n        {\n            // Save or process the account data here\n            // Redirect to next step in wizard or another action\n            return RedirectToAction("RegisterEvent");\n        }\n        \n        return View(accountVM); // Return same view with validation errors\n    }\n\n    [HttpGet]\n    public ActionResult RegisterEvent()\n    {\n        var eventRegistrationViewModel = new EventRegistrationViewModel();\n        return View(eventRegistrationViewModel);\n    }\n\n    [HttpPost]\n    public ActionResult RegisterEvent(EventRegistrationViewModel eventRegVM)\n    {\n        if (ModelState.IsValid)\n        {\n            // Save or process the registration data here\n            // Complete the wizard and redirect to a completion page or home page\n            return RedirectToAction("Success");\n        }\n        \n        return View(eventRegVM); // Return same view with validation errors\n    }\n\n    public ActionResult Success()\n    {\n        ViewBag.Message = "Registration completed successfully!";\n        return View();\n    }\n}