using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class SectionedListView : Form\n{\n    private Panel mainContainer;\n    \n    public SectionedListView()\n    {\n        this.Text = "Office 2007 Style ListView";\n        this.Size = new Size(300, 400);\n        \n        InitializeUI();\n    }\n\n    private void InitializeUI()\n    {\n        mainContainer = new Panel\n        {\n            Dock = DockStyle.Fill,\n            AutoScroll = true,\n            BackColor = Color.LightBlue\n        };\n\n        AddSection("Template Categories", new[] { "Blank Document", "Newsletter", "Brochure" });\n        AddSection("Microsoft Office Online", new[] { "Create Site from Office.com", "SharePoint Products and Technologies", "Excel Web Access User Guide" });\n\n        this.Controls.Add(mainContainer);\n    }\n\n    private void AddSection(string headerText, string[] entries)\n    {\n        Label sectionHeader = new Label\n        {\n            Text = headerText,\n            Font = new Font("Arial", 10, FontStyle.Bold),\n            Dock = DockStyle.Top,\n            AutoSize = false,\n            Width = mainContainer.Width,\n            BackColor = Color.Gray,\n            ForeColor = Color.White\n        };\n        \n        ListBox sectionList = new ListBox\n        {\n            DataSource = entries,\n            Dock = DockStyle.Top,\n            IntegralHeight = true,\n            BorderStyle = BorderStyle.None,\n            BackColor = Color.LightBlue\n        };\n\n        mainContainer.Controls.Add(sectionList);\n        mainContainer.Controls.Add(sectionHeader);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new SectionedListView());\n    }\n}