using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main()\n    {\n        // Specify the name of your application pool\n        string appPoolName = "YourAppPoolName";\n\n        try\n        {\n            using (DirectoryEntry appPools = new DirectoryEntry("IIS://localhost/W3SVC/AppPools"))\n            {\n                using (DirectoryEntry appPool = appPools.Children.Find(appPoolName, "IIsApplicationPool"))\n                {\n                    if (appPool != null)\n                    {\n                        // Recycle the application pool\n                        appPool.Invoke("Recycle", null);\n                        Console.WriteLine($"Application Pool '{appPoolName}' has been recycled.");\n                    }\n                    else\n                    {\n                        Console.WriteLine($"Application Pool '{appPoolName}' not found.");\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}