void Application_Start(object sender, EventArgs e)\n{\n    RegisterRoutes(RouteTable.Routes);\n}\n\npublic static void RegisterRoutes(RouteCollection routes)\n{\n    // Add a general route that maps test/ to test.aspx\n    routes.MapPageRoute("TestRoute", "{name}", "~/test/{name}.aspx");\n    \n    // If you have specific pages, you can add them individually\n    // routes.MapPageRoute("SpecificPageRoute", "specificpage/", "~/test/specificpage.aspx");\n\n    // Add a default route if necessary\n    routes.MapPageRoute("DefaultRoute", "", "~/default.aspx");\n\n    // Optionally handle other URL patterns\n}