using System.DirectoryServices;\nusing System;\n\nstring userDistinguishedName = "CN=John Doe,CN=Users,DC=example,DC=com";\nDirectoryEntry entry = new DirectoryEntry("LDAP://" + userDistinguishedName);\nGuid userGuid = new Guid((byte[])entry.Properties["objectGUID"][0]);\nstring guidString = userGuid.ToString();\n\n// This will provide a constant identifier for the user in Active Directory