using System;\nusing Microsoft.Win32;\n\nclass Program\n{\n    static void Main()\n    {\n        bool net35Installed = IsNet35Installed();\n        Console.WriteLine("Is .NET Framework 3.5 installed? " + net35Installed);\n    }\n\n    private static bool IsNet35Installed()\n    {\n        using (RegistryKey ndpKey = RegistryKey.OpenBaseKey(RegistryHive.LocalMachine, RegistryView.Registry32).OpenSubKey(@"SOFTWARE\Microsoft\NET Framework Setup\NDP\v3.5"))\n        {\n            if (ndpKey != null && ndpKey.GetValue("Install") != null)\n            {\n                int install = (int)ndpKey.GetValue("Install");\n                return install == 1;\n            }\n        }\n        return false;\n    }\n}