using System;\nusing System.Net.Mail;\n\npublic class EmailSender {\n    private const int MaxRetries = 5;\n    private static readonly TimeSpan[] RetryDelays = { TimeSpan.FromSeconds(2), TimeSpan.FromSeconds(4), TimeSpan.FromSeconds(8), TimeSpan.FromSeconds(16), TimeSpan.FromSeconds(32) };\n\n    public bool SendEmail(string toAddress, string subject, string body) {\n        for (int i = 0; i < MaxRetries; i++) {\n            try {\n                using (var client = new SmtpClient("smtp.example.com")) {\n                    var mailMessage = new MailMessage("from@example.com", toAddress, subject, body);\n                    client.Send(mailMessage);\n                }\n                return true;\n            } catch (SmtpException e) when ((i + 1 < MaxRetries)) { // Retry condition\n                Console.WriteLine($"Attempt #{i+1} failed, retrying in {RetryDelays[i]}: " + e.Message);\n                System.Threading.Thread.Sleep(RetryDelays[i]);\n            }\n        }\n        return false;\n    }\n\n    public void RegisterUserAndSendEmail(string userEmail) {\n        if (!SendEmail(userEmail, "Welcome!", "Your account has been created.")) {\n            Console.WriteLine("Failed to send email. User registered without notification.");\n            // Further handling (e.g., logging)\n        } else {\n            Console.WriteLine("Registration and email sent successfully.");\n        }\n    }\n}