using Mono.Cecil;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load your assembly\n        var assembly = AssemblyDefinition.ReadAssembly(@"path-to-your-assembly.exe");\n\n        // Access a specific type and method in the module\n        TypeDefinition myClass = assembly.MainModule.GetType("Namespace.MyClass");\n        MethodDefinition myMethod = myClass.GetMethod("MyMethod");\n\n        // Insert an IL instruction at the beginning of the method\n        var ilProcessor = myMethod.Body.GetILProcessor();\n        var firstInstruction = myMethod.Body.Instructions[0];\n        ilProcessor.InsertBefore(firstInstruction, ilProcessor.Create(OpCodes.Nop));\n\n        // Write changes back to the assembly\n        assembly.Write(@"path-to-your-modified-assembly.exe");\n    }\n}