using System;\nusing SHDocVw; // COM reference to Microsoft Internet Controls\nusing mshtml;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new instance of Internet Explorer and navigate to a webpage\n        var ie = new InternetExplorer();\n        ie.Visible = true;\n        ie.Navigate("http://example.com");\n\n        // Wait for the page to load completely\n        while (ie.Busy || ie.ReadyState != tagREADYSTATE.READYSTATE_COMPLETE)\n            System.Threading.Thread.Sleep(100);\n\n        // Get a reference to the document object\n        var doc = (IHTMLDocument2)ie.Document;\n\n        // Example: Click a button with known ID\n        IHTMLElement element = doc.getElementById("submitButton");\n        if (element != null)\n        {\n            ((mshtml.IHTMLElementCollection)doc.all).item("submitButton", 0).click();\n        }\n\n        // Example: Set value of an input field with known name\n        mshtml.IHTMLInputElement inputElement = (mshtml.IHTMLInputElement)doc.getElementById("textInput");\n        if (inputElement != null)\n        {\n            inputElement.value = "Hello, World!";\n        }\n    }\n}