protected void ValidatePage()\n{\n    if (!Page.IsValid)\n    {\n        foreach (BaseValidator validator in Page.GetValidators())\n        {\n            if (!validator.IsValid)\n            {\n                // You can access the ControlToValidate property to get the ID of the control being validated.\n                string controlId = validator.ControlToValidate;\n                WebControl control = (WebControl)FindControl(controlId);\n                \n                if (control != null)\n                {\n                    // Apply your style or logic here\n                    control.Style.Add("border", "2px solid red"); // Example: Highlighting the invalid input field\n                }\n            }\n        }\n    }\n}