using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string message = "";\n\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        using (SqlCommand cmd = new SqlCommand("GetUserMessage", conn))\n        {\n            cmd.CommandType = System.Data.CommandType.StoredProcedure;\n            cmd.Parameters.AddWithValue("@UserID", 123);\n            SqlParameter userMessageParam = cmd.Parameters.Add("@UserMessage", System.Data.SqlDbType.NVarChar, 500);\n            userMessageParam.Direction = System.Data.ParameterDirection.Output;\n\n            conn.Open();\n            cmd.ExecuteNonQuery();\n\n            message = (string)userMessageParam.Value;\n        }\n\n        Console.WriteLine(message); // Outputs the message from the stored procedure\n    }\n}