using System.Web.Mvc;\nusing System.Web.Mvc.Html;\n\nnamespace YourNamespace.Helpers\n{\n    public static class ValidationHelperExtensions\n    {\n        public static MvcHtmlString CustomValidationSummary(this HtmlHelper htmlHelper, string message)\n        {\n            if (htmlHelper.ViewData.ModelState.IsValid)\n            {\n                return MvcHtmlString.Empty;\n            }\n\n            var summary = htmlHelper.ValidationSummary(message);\n            var divTag = new TagBuilder("div");\n            divTag.AddCssClass("validation-summary");\n            divTag.InnerHtml = summary.ToString();\n            \n            return MvcHtmlString.Create(divTag.ToString());\n        }\n    }\n}