using System;\nusing IronPython.Hosting;\nusing Microsoft.Scripting.Hosting;\n\nclass ScriptHost\n{\n    public void ExposedMethod(string message)\n    {\n        Console.WriteLine("Called from Python: " + message);\n    }\n\n    public void RunScript()\n    {\n        // Create a script engine for IronPython\n        ScriptEngine engine = Python.CreateEngine();\n\n        // Get the scope of execution\n        ScriptScope scope = engine.CreateScope();\n\n        // Set this instance to be accessible in Python as 'host'\n        scope.SetVariable("host", this);\n\n        // Define some simple Python code that uses the exposed method\n        const string scriptCode = @"\nprint('Hello from IronPython!')\nhost.ExposedMethod('This is a test message from Python')\n";\n\n        // Execute the Python code\n        engine.Execute(scriptCode, scope);\n    }\n\n    static void Main(string[] args)\n    {\n        ScriptHost host = new ScriptHost();\n        host.RunScript();\n    }\n}