using System;\nusing System.Text.RegularExpressions;\n\npublic class HtmlValidator\n{\n    public static bool ContainsHtml(string input)\n    {\n        if (string.IsNullOrEmpty(input))\n            return false;\n\n        // Regex pattern to match common HTML tags.\n        string htmlTagPattern = "<.*?>";\n\n        // Check for the presence of any HTML tag-like structure.\n        return Regex.IsMatch(input, htmlTagPattern);\n    }\n\n    public static bool IsPlainText(string input)\n    {\n        return !ContainsHtml(input);\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string testString = "Hello, this is a <strong>test</strong> string.";\n        Console.WriteLine(HtmlValidator.IsPlainText(testString)); // Output: False\n\n        string plainTextString = "Hello, this is a test string.";\n        Console.WriteLine(HtmlValidator.IsPlainText(plainTextString)); // Output: True\n    }\n}