using System;\nusing Microsoft.Deployment.WindowsInstaller;\n\npublic class CustomActions\n{\n    [CustomAction]\n    public static ActionResult SetGacPath(Session session)\n    {\n        // Define your assembly details\n        string assemblyName = "YourAssemblyName";\n        string version = "1.0.0.0";  // replace with your version\n        string publicKeyToken = "yourpublickeytoken";  // replace with your public key token\n\n        // Construct the path based on whether it's a 32-bit system or targeting a 32-bit application on a 64-bit system\n        string gacPath;\n        if (Environment.Is64BitOperatingSystem && !Environment.Is64BitProcess)\n            gacPath = Environment.GetFolderPath(Environment.SpecialFolder.Windows) + @"\Microsoft.NET\assembly\GAC_32\" + assemblyName + @"\v4.0_" + version + "__" + publicKeyToken + "\\";\n        else\n            gacPath = Environment.GetFolderPath(Environment.SpecialFolder.Windows) + @"\assembly\GAC_MSIL\" + assemblyName + @"\" + version + @"\" + publicKeyToken + "\\";\n\n        session["ASSEMBLY_GAC_PATH"] = gacPath;\n        return ActionResult.Success;\n    }\n}