[Test]\npublic void CalculateFutureDate_ShouldReturnKnownValue_WhenGivenFixedDate()\n{\n    // Arrange\n    var mockDateTimeProvider = new MockDateTimeProvider(new DateTime(2023, 1, 1));\n    var someService = new SomeService(mockDateTimeProvider);\n    \n    // Act\n    var result = someService.CalculateFutureDate(5);\n    \n    // Assert\n    Assert.AreEqual(new DateTime(2023, 1, 6), result);\n}