protected void Page_PreRender(object sender, EventArgs e)\n{\n    // Registering a handler for the PreRenderComplete event\n    this.PreRenderComplete += new EventHandler(Page_PreRenderComplete);\n}\n\nvoid Page_PreRenderComplete(object sender, System.EventArgs e)\n{\n    StringWriter sw = new StringWriter();\n    HtmlTextWriter htwWriter = new HtmlTextWriter(sw);\n\n    // Render the page content to a string\n    base.Render(htwWriter);\n    string renderedHtml = sw.ToString();\n\n    // Modify the HTML content\n    if (chk_CheckAll.Checked)\n    {\n        // Use regular expression or string replacements to change checkbox properties\n        renderedHtml = Regex.Replace(renderedHtml, \n            @"<input type=""checkbox""\s*\/>", \n            "<input type=\"checkbox\" disabled checked />", \n            RegexOptions.IgnoreCase);\n    }\n\n    // Clear the existing response output and write the modified HTML\n    this.Response.Clear();\n    Response.Write(renderedHtml);\n}