using System;\nusing System.Collections.Generic;\nusing HtmlAgilityPack;\n\npublic class ImageExtractor\n{\n    public static List<string> ExtractImageSources(string htmlContent)\n    {\n        var imageUrls = new List<string>();\n\n        // Create a new instance of HtmlDocument\n        var doc = new HtmlDocument();\n\n        // Load the HTML content into the document\n        doc.LoadHtml(htmlContent);\n\n        // Select all <img> nodes\n        var imgNodes = doc.DocumentNode.SelectNodes("//img[@src]");\n\n        if (imgNodes != null)\n        {\n            foreach (var node in imgNodes)\n            {\n                // Get the src attribute value and add it to the list\n                var srcValue = node.GetAttributeValue("src", string.Empty);\n                imageUrls.Add(srcValue);\n            }\n        }\n\n        return imageUrls;\n    }\n\n    public static void Main()\n    {\n        string htmlContent = "<html><body><img src='http://example.com/image1.jpg' /><img src='http://example.com/image2.png' /></body></html>";\n        var sources = ExtractImageSources(htmlContent);\n\n        foreach (var source in sources)\n        {\n            Console.WriteLine(source);\n        }\n    }\n}