using (OracleConnection conn = new OracleConnection("your_connection_string"))\n    {\n        conn.Open();\n        string sql = "INSERT INTO your_table (id, column1, column2) VALUES (my_sequence.NEXTVAL, :column1, :column2) RETURNING id INTO :newId";\n        \n        using (OracleCommand cmd = new OracleCommand(sql, conn))\n        {\n            cmd.Parameters.Add(new OracleParameter(":column1", OracleDbType.Varchar2)).Value = "value1";\n            cmd.Parameters.Add(new OracleParameter(":column2", OracleDbType.Varchar2)).Value = "value2";\n            \n            // Output parameter to hold the new ID\n            OracleParameter returnParam = cmd.Parameters.Add("newId", OracleDbType.Int32);\n            returnParam.Direction = ParameterDirection.Output;\n\n            cmd.ExecuteNonQuery();\n\n            int newId = Convert.ToInt32(returnParam.Value);\n            // Use newId as needed\n        }\n    }