using System;\nusing System.IO;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        string fileUrl = "https://example.com/file.zip";\n        string destinationPath = @"C:\Downloads\file.zip";\n\n        try\n        {\n            using (WebClient client = new WebClient())\n            {\n                // Optionally, you can configure this to use specific certificates or handle exceptions.\n                ServicePointManager.ServerCertificateValidationCallback += \n                    delegate(object s, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n                    { return true; }; // In a real-world scenario, validate the certificate.\n\n                client.DownloadFile(fileUrl, destinationPath);\n            }\n        }\n        catch (WebException ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n\n        Console.WriteLine("Download completed.");\n    }\n}