using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program {\n    static void Main() {\n        XDocument doc = XDocument.Load("yourfile.xml");\n        var results = from elem in doc.Descendants("YourElement")\n                      select (string)elem.Attribute("SomeAttribute");\n\n        foreach (var result in results) {\n            Console.WriteLine(result);\n        }\n    }\n}