// Simple MyGeneration Template Example for creating a .NET entity class\n\nusing System;\n\n<%\n// Get the table definition\nTable table = model.GetTable(Request["TableName"]);\n\n// Start the class declaration\n%>namespace MyApp.Entities {\n    public class <%= table.ClassName %> {\n        // Generate properties based on columns in the database table\n        <% foreach (Column column in table.Columns) { %>\n        public <%= column.DataType.ToString().ToLower() %> <%= column.PropertyName %> { get; set; }\n        <% } %>\n    }\n}\n<%\n// End the template code block\n%>