protected void btnUpload_Click(object sender, EventArgs e)\n   {\n       if (fileUploadControl.HasFiles)\n       {\n           string fileName = Path.GetFileName(fileUploadControl.FileName);\n           string path = Server.MapPath("~/../SharedFiles/") + fileName;\n           fileUploadControl.SaveAs(path);\n\n           // Save to database\n           string connString = ConfigurationManager.ConnectionStrings["YourDBConnection"].ConnectionString;\n           using (SqlConnection con = new SqlConnection(connString))\n           {\n               SqlCommand cmd = new SqlCommand("INSERT INTO Files (FileName, FilePath) VALUES (@Name, @Path)", con);\n               cmd.Parameters.AddWithValue("@Name", fileName);\n               cmd.Parameters.AddWithValue("@Path", path.Replace(Server.MapPath("~"), ""));\n               \n               try\n               {\n                   con.Open();\n                   cmd.ExecuteNonQuery();\n               }\n               catch (Exception ex)\n               {\n                   // Handle error\n               }\n           }\n       }\n   }