class Result {\n    public bool Success { get; set; }\n    public string Value { get; set; }\n\n    public Result(bool success, string value = null) {\n        Success = success;\n        Value = value;\n    }\n}\n\nResult Walk_r(TreeNode node) {\n    if (BaseCase(node)) {\n        return new Result(true, "ComputedValue");\n    }\n\n    foreach (var child in node.Children) {\n        var result = Walk_r(child);\n        if (result.Success) {\n            return result; // Return the successful result immediately\n        }\n    }\n\n    return new Result(false); // Indicate failure or continue\n}\n\n// Usage\nResult result = Walk_r(tree);\nif (result.Success) {\n    Console.WriteLine("Success: " + result.Value);\n} else {\n    Console.WriteLine("No success found.");\n}