using System;\nusing System.IO;\nusing System.Web.UI;\n\npublic class HtmlGenerator\n{\n    public void GenerateHtmlFromAspx(string url, string outputPath)\n    {\n        // Create a new instance of the HttpRuntime\n        var page = new HiddenPage();\n        \n        // Render the aspx page to a StringWriter\n        using (var writer = new StringWriter())\n        {\n            HttpContext.Current.Server.Execute(url, writer);\n            string htmlContent = writer.ToString();\n\n            // Write the rendered content to an HTML file\n            File.WriteAllText(outputPath, htmlContent);\n        }\n    }\n\n    private class HiddenPage : Page { }\n}\n\n// Usage\nvar generator = new HtmlGenerator();\ngenerator.GenerateHtmlFromAspx("~/YourPage.aspx", @"C:\Output\YourPage.html");