using System;\nusing System.IO;\n\nprotected void SubmitButton_Click(object sender, EventArgs e)\n{\n    string filePath = Server.MapPath("~/Pics/Image1.jpg");\n\n    // Check if the file exists before trying to delete it.\n    if (File.Exists(filePath))\n    {\n        try\n        {\n            File.Delete(filePath);\n        }\n        catch (Exception ex)\n        {\n            // Log or handle any exceptions that occur during deletion.\n            // For example, log to a file or error logging system.\n            Console.WriteLine("Error deleting the file: " + ex.Message);\n        }\n    }\n    else\n    {\n        // Handle the case when the file does not exist if necessary.\n        Console.WriteLine("The file does not exist.");\n    }\n}