using System.Security.Cryptography;\nusing System.IO;\n\npublic static void EncryptFile(string inputFile, string outputFile)\n{\n    using (Aes aes = Aes.Create())\n    {\n        aes.Key = Convert.FromHexString("0123456789ABCDEF0123456789ABCDEF");\n        aes.IV = Convert.FromHexString("0123456789ABCDEF");\n\n        ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\n\n        using (FileStream fsOutput = new FileStream(outputFile, FileMode.Create))\n        using (CryptoStream cs = new CryptoStream(fsOutput, encryptor, CryptoStreamMode.Write))\n        {\n            byte[] bytesToEncrypt = File.ReadAllBytes(inputFile);\n            cs.Write(bytesToEncrypt, 0, bytesToEncrypt.Length);\n        }\n    }\n}