public class AsyncUsernameValidator : BaseValidator\n{\n    public string UsernameTextBoxID { get; set; }\n\n    protected override void AddAttributesToRender(HtmlTextWriter writer)\n    {\n        base.AddAttributesToRender(writer);\n        writer.AddAttribute("data-username-tb", UsernameTextBoxID);\n        writer.AddAttribute("data-validation-url", ResolveUrl("~/CheckUsername.aspx"));\n    }\n\n    protected override bool EvaluateIsValid()\n    {\n        // Server-side validation would go here.\n        // This can be a simple check against the database\n        string username = GetControlValidationValue(UsernameTextBoxID) ?? "";\n        return !IsUsernameTaken(username);\n    }\n\n    private bool IsUsernameTaken(string username)\n    {\n        // Example logic - replace with actual data access code\n        return UsersDataSource.Any(u => u.Username == username);\n    }\n}