using System;\nusing System.Data.OleDb;\n\npublic void AddToDatabase(string[] WordArray, int Good, int Bad, int Remove)\n{\n    string connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=doom_calc_dict.mdb;";\n    \n    using (OleDbConnection connection = new OleDbConnection(connectionString))\n    {\n        connection.Open();\n        string sql = "INSERT INTO WordDef (Word, Good, Bad, Remove) VALUES (?, ?, ?, ?)";\n        \n        using (OleDbCommand command = new OleDbCommand(sql, connection))\n        {\n            command.Parameters.AddWithValue("@Word", DBNull.Value);\n            command.Parameters.AddWithValue("@Good", Good);\n            command.Parameters.AddWithValue("@Bad", Bad);\n            command.Parameters.AddWithValue("@Remove", Remove);\n\n            for (int WordCount = 0; WordCount < WordArray.Length; WordCount++)\n            {\n                command.Parameters["@Word"].Value = WordArray[WordCount];\n                \n                try\n                {\n                    command.ExecuteNonQuery();\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine("Error: " + ex.Message);\n                }\n            }\n        }\n    }\n}