using System;\nusing System.IO;\nusing System.Text;\n\npublic class UnicodeMessageReader\n{\n    public static void Main()\n    {\n        // Example byte array representing "test?" in UTF-16 encoding\n        byte[] bytes = { 0x74, 0x00, 0x65, 0x00, 0x73, 0x00, 0x74, 0x00, 0x3F, 0x00 };\n\n        using (var memoryStream = new MemoryStream(bytes))\n        {\n            using (var binaryReader = new BinaryReader(memoryStream, Encoding.BigEndianUnicode))\n            {\n                // Read 5 characters (10 bytes for UTF-16)\n                char[] chars = binaryReader.ReadChars(5);\n                string result = new string(chars);\n\n                Console.WriteLine(result); // Output: test?\n            }\n        }\n    }\n}