using System;\nusing System.Diagnostics;\n\npublic class TimestampedTraceListener : TraceListener\n{\n    public override void WriteLine(string message)\n    {\n        base.WriteLine($"{DateTime.Now} {message}");\n    }\n\n    public override void Write(string message)\n    {\n        base.Write($"{DateTime.Now} {message}");\n    }\n}\n\n// Usage in the application startup or configuration\nclass Program\n{\n    static void Main()\n    {\n        Trace.Listeners.Clear();\n        Trace.Listeners.Add(new TimestampedTraceListener());\n\n        Trace.WriteLine("Something wrong!");\n        // Output will be: <current_timestamp> Something wrong!\n    }\n}