using System;\nusing System.IO;\nusing System.Web;\n\npublic class MyHttpModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.BeginRequest += new EventHandler(OnBeginRequest);\n    }\n\n    private void OnBeginRequest(object sender, EventArgs e)\n    {\n        HttpApplication app = (HttpApplication)sender;\n        var requestUrl = app.Request.Url.LocalPath;\n\n        string fileExtension = Path.GetExtension(requestUrl);\n\n        if (!string.IsNullOrEmpty(fileExtension))\n        {\n            // Remove the leading dot\n            fileExtension = fileExtension.Substring(1);\n            // Now you can use fileExtension to determine what type of file it is\n            switch (fileExtension.ToLower())\n            {\n                case "css":\n                    Console.WriteLine("This is a CSS file.");\n                    break;\n                case "js":\n                    Console.WriteLine("This is a JavaScript file.");\n                    break;\n                // Add additional cases as needed\n                default:\n                    Console.WriteLine($"Unknown file type: {fileExtension}");\n                    break;\n            }\n        }\n    }\n\n    public void Dispose() {}\n}