// Example using CDO 1.2 to access Exchange 2003 (note that newer libraries are recommended)\nusing Microsoft.CDO.ExM;\n\nCDOEXFolder calendar = null;\ntry\n{\n    CDOEXSession session = new CDOEXSession();\n    session.Logon("http://yourserver/exchange", "username", "password", true, true);\n    calendar = (CDOEXFolder)session.Folders("Calendar");\n    Console.WriteLine(calendar.Items.Count + " appointments found in the calendar.");\n}\ncatch (Exception ex)\n{\n    Console.WriteLine("Error accessing Exchange server: " + ex.Message);\n}\nfinally\n{\n    if (calendar != null) Marshal.ReleaseComObject(calendar);\n}