using System;\nusing System.Data;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        // Creating a sample DataSet with DataTable\n        DataSet dataSet = new DataSet();\n        DataTable table = new DataTable("Products");\n        \n        table.Columns.Add("Id", typeof(int));\n        table.Columns.Add("Name", typeof(string));\n        table.Columns.Add("Price", typeof(decimal));\n\n        // Adding rows to the DataTable\n        table.Rows.Add(1, "Laptop", 999.99m);\n        table.Rows.Add(2, "Phone", 450.75m);\n\n        dataSet.Tables.Add(table);\n\n        // Querying the DataTable using LINQ\n        var query = from row in dataSet.Tables["Products"].AsEnumerable()\n                    where row.Field<decimal>("Price") > 300\n                    select new\n                    {\n                        Id = row.Field<int>("Id"),\n                        Name = row.Field<string>("Name")\n                    };\n\n        foreach (var item in query)\n        {\n            Console.WriteLine($"ID: {item.Id}, Name: {item.Name}");\n        }\n    }\n}