using System;\nusing System.Linq;\nusing GlobExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define your search root directory and pattern\n        string rootDir = "path/to/your/root";\n        var pattern = new Glob("dir1/dir2/**/SVN/*");\n\n        // Get all matching files\n        var matches = Directory.GetFiles(rootDir, "*.*", SearchOption.AllDirectories)\n                             .Where(file => pattern.IsMatch(file));\n\n        foreach (var file in matches)\n        {\n            Console.WriteLine(file);\n        }\n    }\n}