// Inserting a new row\nDataRow newRow = table.NewRow();\nnewRow["Id"] = 3;\nnewRow["Name"] = "Tablet";\nnewRow["Price"] = 299.99m;\ntable.Rows.Add(newRow);\n\n// Deleting a row\nvar rowToDelete = table.AsEnumerable().FirstOrDefault(x => x.Field<int>("Id") == 1);\nif (rowToDelete != null)\n{\n    rowToDelete.Delete();\n}\n\n// Updating a row\nvar rowToUpdate = table.AsEnumerable().FirstOrDefault(x => x.Field<int>("Id") == 2);\nif (rowToUpdate != null)\n{\n    rowToUpdate["Price"] = 470.50m;\n}